user node[:hopsbench][:user] do
  action :create
  supports :manage_home => true
  home "/home/#{node[:hopsbench][:user]}"
  shell "/bin/bash"
  not_if "getent passwd #{node[:hopsbench]['user']}"
end

group node[:hopsbench][:group] do
  action :modify
  members ["#{node[:hopsbench][:user]}"]
  append true
end


